      align-items: center;
      gap: 1em;
   }

   &__container {
      grid-template-columns: 1.6fr 1fr;
      gap: 2em;
      align-items: flex-start;
   }

   &__card {
      gap: 1em;
      margin-bottom: var(--mb-2);
   }

   &__device {
      width: 80px;
      gap: 1em;
      align-items: center;
      background-color: var(--first-color-alt);
      padding: 0.5em 1em;
      color: #fff;
   }

   &__image {
      width: 400px;
      height: 300px;
      img {
         height: 100%;
         border-radius: 1em;
         object-fit: cover;
      }
   }

   &__content {
      position: relative;
      height: 300px;
      h2 {
         margin-top: 1em;
         color: #fff;
         margin-bottom: var(--mb-0-5);
         font-size: var(--h3-font-size);
      }

      p {
         color: var(--text-color-light);
         font-size: var(--small-font-size);
      }
   }

   &__app {
      align-items: center;
      position: absolute;
      gap: 1em;
      bottom: 1em;
      color: var(--text-color-light);

      img {
         width: 30px;
      }
   }

   &__letter {
      background-color: #212045;
      padding: 1.2em;
      border-radius: 10px;

      h4 {
         font-size: var(--h3-font-size);
         margin-bottom: var(--mb-1);
         color: #fff;
      }

      p {
         color: var(--text-color-light);
         font-size: var(--small-font-size);
         margin-bottom: var(--mb-1);
      }

      input {
         background-color: var(--first-color-alt);
         border: none;
         padding: 0.5em;
         color: var(--text-color-light);
         margin-bottom: var(--mb-2);
      }

      button {
         background-image: linear-gradient(
            to left,
            hsl(228, 74%, 49%),
            hsl(232, 86%, 59%),
            hsl(232, 86%, 59%)
         );
         color: var(--clr-light);
         padding: 0.5em 1.5em;
         border-radius: 10px;
      }
   }

   &__icons2 {
      justify-content: center;
      align-items: center;
      background-color: var(--body-color);
      width: 50px;
      height: 50px;
      margin-bottom: var(--mb-1-5);
   }
}

